example.code:
// Defaults: IndentCharCount = 2, LineLimit = 80, WrapEnabled = false.
var hb = new HTMLBuider();

var copyright = new string[]
{
  "Copyright Line 1",
  "Copyright Line 2",
};
hb.HTMLBegin(null, copyright, "fileName.html");
var result = hb.ToString();

// result:
// &lt;!DOCTYPE html&gt;
// &lt;!-- Copyright Line 1 --&gt;
// &lt;!-- Copyright Line 1 --&gt;
// &lt;!-- fileName.html --&gt;
// &lt;html lang="en" xmlns="http://www.w3.org/1999/xhtml"&gt;
// &lt;head&gt;

// Defaults: IndentCharCount = 2, LineLimit = 80, WrapEnabled = false.
var hb = new HTMLBuider();

var copyright = new string[]
{
  "Copyright Line 1",
  "Copyright Line 2",
};
var result = hb.GetHTMLBegin(null, copyright, "fileName.html");

// result:
// &lt;!DOCTYPE html&gt;
// &lt;!-- Copyright Line 1 --&gt;
// &lt;!-- Copyright Line 1 --&gt;
// &lt;!-- fileName.html --&gt;
// &lt;html lang="en" xmlns="http://www.w3.org/1999/xhtml"&gt;
// &lt;head&gt;

// Defaults: IndentCharCount = 2, LineLimit = 80, WrapEnabled = false.
var hb = new HTMLBuider();

var result = hb.GetHTMLEnd();

// result:
// &lt;/body&gt;
// &lt;/html&gt;

// Defaults: IndentCharCount = 2, LineLimit = 80, WrapEnabled = false.
var hb = new HTMLBuider();

var result = hb.StartAttribs();

// result:
var result = new Attributes()
{
  { "lang", "en" },
  { "xmlns", "http://www.w3.org/1999/xhtml" },
};

// Defaults: IndentCharCount = 2, LineLimit = 80, WrapEnabled = false.
var hb = new HTMLBuider();

var result = hb.TableAttribs();

// result:
var result = new Attributes()
{
  { "border", "1" },
  { "cellspacing", "0" },
  { "cellpadding", "2" },
};
